import React, { useState, useEffect, useContext } from "react";
import {baseURLAPI ,baseURL} from "../../Global";
import {
  Keyboard,
  TouchableOpacity,
  StyleSheet,
  Text,
  View,
  Image,Platform,
  Button,
  div,
  Icon,
  TextInput,
} from "react-native";
import * as ImagePicker from 'expo-image-picker';
import * as Google from "expo-google-app-auth";
import AsyncStorage from "@react-native-community/async-storage";
const axios = require("axios");  
import Constants from 'expo-constants';

export default class LoginScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      signedIn: false,
      name: "",
      photoUrl: "",
    };
  }
  signUpUser = async (email, password) => {
    Keyboard.dismiss();

    const body = {
      TX_USER_NAME: email,
      TX_USER_EMAIL: email,
      TX_USER_PASSWORD: password,
    };
    console.log(baseURLAPI);
 
  
  axios.post(baseURLAPI+"/auth/register",body) .then((response) => {
  
    if (response.data.jwtToken) {

              AsyncStorage.setItem("jwtToken", response.data.jwtToken);

            this.setState({
            signedIn: true,
            name:response.data.jwtToken ,
            photoUrl: "result.user.photoUrl",
          });
        }
      })
      .catch((error) => {
        console.log("error");

        console.log(error);
      });
  };
  signInUser = async (email, password) => {
    Keyboard.dismiss();

    const body = {
      TX_USER_NAME: email,
      TX_USER_EMAIL: email,
      TX_USER_PASSWORD: password,
    };
    console.log(baseURLAPI);
   axios.post(baseURLAPI+"/auth/login",body) .then((response) => {
 
     
    
    if (response.data.jwtToken) {
          AsyncStorage.setItem("jwtToken",response.data.jwtToken);

        this.setState({
        signedIn: true,
        name:response.data.jwtToken ,
        photoUrl: "result.user.photoUrl",
      });
 
        }
      })
      .catch((error) => {
        console.log("error");

        console.log(error);
      });
  };

  signIn = async () => {
    try {
      const result = await Google.logInAsync({
        androidClientId:
          "770480272379-786um8vmqi9p35ingldm4celsrk2jshi.apps.googleusercontent.com",
        iosClientId:
          "770480272379-rlr3mcpthnr02t3rul9ls1u5p4k2bsm6.apps.googleusercontent.com",
        scopes: ["profile", "email"],
      });

      if (result.type === "success") {
        this.setState({
          signedIn: true,
          name: result.user.name,
          photoUrl: result.user.photoUrl,
        });
      } else {
        console.log("cancelled");
      }
    } catch (e) {
      console.log("error", e);
    }
  };
  render() {
    return (
      <View style={styles.container}>
        {this.state.signedIn ? (
          <LoggedInPage name={this.state.name} photoUrl={this.state.photoUrl} />
        ) : (
          <LoginPage
            signIn={this.signIn}
            signUpUser={this.signUpUser}
            signInUser={this.signInUser}
          />
        )}
      </View>
    );
  }
}

const LoginPage = (props) => {
  const [signupView, setsignupView] = useState(false);
  const [email, setemailCredencials] = useState("");
  const [password, setpasswordCredencials] = useState(""); 
  
  const [name, setnameCredencials] = useState("");
  const [picture, setpictureCredencials] = useState("");
  const [phone, setphoneCredencials] = useState("");


  const [url, seturlCredencials] = useState(""); 
  const [image, setImage] = useState(null);

  useEffect(() => {
    (async () => {
      if (Platform.OS !== 'web') {
        const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== 'granted') {
          alert('Sorry, we need camera roll permissions to make this work!');
        }
      }
    })();
  }, []);

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4,4],
      quality: 1,
    });

    console.log(result);

    if (!result.cancelled) {
      setImage(result.uri);
    }
  };

   const viewSignUp = () => {
    setsignupView(!signupView);
  };
  const imageadd = () => {
    imagePicker.open({
      takePhoto: true,
      useLastPhoto: true,
      chooseFromLibrary: true
  }).then(({ uri, width, height }) => {
      console.log('image asset', uri, width, height);
      seturlCredencials (uri);
      console.log('image asset', uri, width, height);


  }, (error) => {
      // Typically, user cancel  
      console.log('error', error);
  });
  };
 
  
  return (
    <View>
      {signupView ? (
        <View>
          <Text style={styles.header}>Register</Text>
         
          <Button title="Pick an image from camera roll" onPress={pickImage} />
      {image && <Image source={{ uri: image }} style={{ width: 200, height: 200 }} />}
          <TouchableOpacity
            onPress={() => props.signUpUser(email, password)}
            style={styles.UserButton}
          >
            <Text
              style={{
                color: "#fff",
              }}
            >
              image
            </Text>
          </TouchableOpacity>
  
<TextInput
            style={styles.TextInput}
            onChangeText={(name) => setnameCredencials(name)}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Name"
            placeholderTextColor="#002f6c"
            selectionColor="#fff"
            keyboardType="default"
          />




          <TextInput
            style={styles.TextInput}
            onChangeText={(email) => setemailCredencials(email)}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Email"
            placeholderTextColor="#002f6c"
            selectionColor="#fff"
            keyboardType="email-address"
          />

         
          <TextInput
            style={styles.TextInput}
            onChangeText={(phone) => setphoneCredencials(phone)}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Phone"
            placeholderTextColor="#002f6c"
            selectionColor="#fff"
            keyboardType="numeric"
          />



          <TextInput
            style={styles.TextInput}
            onChangeText={(password) => setpasswordCredencials(password)}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Password"
            placeholderTextColor="#002f6c"
            selectionColor="#fff"
            secureTextEntry={true}
          />
          <TouchableOpacity
            onPress={() => props.signUpUser(email, password)}
            style={styles.UserButton}
          >
            <Text
              style={{
                color: "#fff",
              }}
            >
              Register
            </Text>
          </TouchableOpacity>

          <TouchableOpacity onPress={ viewSignUp} style={styles.text}>
            <Text
              style={{
                color: "#fff",
              }}
            >
              Sign-In
            </Text>
          </TouchableOpacity>
        </View>
      ) : (
        <View>
          <Text style={styles.header}>Login</Text>

          <TextInput
            style={styles.TextInput}
            onChangeText={(email) => setemailCredencials(email)}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Email"
            placeholderTextColor="#002f6c"
            selectionColor="#fff"
            keyboardType="email-address"
          />

          <TextInput
            style={styles.TextInput}
            onChangeText={(password) => setpasswordCredencials(password)}
            underlineColorAndroid="rgba(0,0,0,0)"
            placeholder="Password"
            placeholderTextColor="#002f6c"
            selectionColor="#fff"
            secureTextEntry={true}
          />
          <TouchableOpacity
            onPress={() => props.signInUser(email, password)}
            style={styles.UserButton}
          >
            <Text style={{ color: "#fff" }}>Login</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={  viewSignUp  } style={styles.text}>
            <Text
              style={{
                color: "#000000",
              }}
            >
              Not a user? Sign Up
            </Text>
          </TouchableOpacity>
          <TouchableOpacity
            onPress={() => props.signIn()}
            style={styles.googleButton}
          >
            <Text style={{ color: "#fff" }}>Sign in with Google</Text>
          </TouchableOpacity>
        </View>
      )}
    </View>
  );
};

