  const [password, setpasswordCredencials] = useState("");

  const [name, setnameCredencials] = useState(""); 
  const [phone, setphoneCredencials] = useState("");

  const [url, seturlCredencials] = useState("");
  const [image, setImage] = useState(null);

  useEffect(() => {
    (async () => {
      if (Platform.OS !== "web") {
        const {
          status,
        } = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== "granted") {
          alert("Sorry, we need camera roll permissions to make this work!");
        }
      }
    })();
  }, []);

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      base64: true,
      aspect: [4, 4],
      quality: 1,
    });

    console.log(result);
    console.log("image asset", result);
    if (!result.cancelled) {
      setImage(result.uri);
    }
  };

  const viewSignUp = () => {
    setsignupView(!signupView);
  };
  const imageadd = () => {
    imagePicker
      .open({
        takePhoto: true,
        useLastPhoto: true,
        chooseFromLibrary: true,
      })
      .then(
        ({ uri, width, height }) => {
          console.log("image asset", uri, width, height);
          seturlCredencials(uri);
          console.log("image asset", uri.base64, width, height);
        },
        (error) => {
          // Typically, user cancel
          console.log("error", error);
        }
      );
  };

  return (
    <View>
      {signupView ? (
        <View>
          <Text style={styles.header}>Register</Text>

          <TouchableOpacity
            style={{
              alignContent: "center",
              alignItems: "center",
            }}
          >
            {image ? (
              <TouchableOpacity
                style={{
                  alignContent: "center",
                  alignItems: "center",
                }}
              >
                <Image
                  source={{ uri: image }}
                  style={{ width: 100, height: 100, borderRadius: 50 }}
