import React, { useState, useEffect, useContext } from "react";
import { baseURLAPI, baseURL } from "../../Global";
import {
  Keyboard,
  TouchableOpacity,
  StyleSheet,
  Text,
  View,
  Image,
  Platform,
  Button,
  div,
  Icon,
  TextInput,
  useColorScheme,
} from "react-native";
import * as ImagePicker from "expo-image-picker";
import * as Google from "expo-google-app-auth";
import AsyncStorage from "@react-native-community/async-storage";
const axios = require("axios");
import Constants from "expo-constants";
import styles from "../styles/styles";
import Homepage from "./Homepage";

export default class LoginScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      signedIn: false,
      
    };
  }
  setLogin = async () => {
    this.setState({
      signedIn: true,
    });
  };

  render() {
    return (
      <View style={styles.container}>
        {this.state.signedIn ? (
          <LoggedInPage />
        ) : (
          <LoginPage setLogin={this.setLogin} />
        )}
      </View>
    );
  }
}

const LoginPage = (props) => {
  const [signupView, setsignupView] = useState(false);
  const [email, setemailCredencials] = useState("");
  const [password, setpasswordCredencials] = useState("");

  const [name, setnameCredencials] = useState("");
  const [phone, setphoneCredencials] = useState("");
  const  [ RegisterErrorList, setRegisterErrorList] = useState("");

  const  [ SignInErrorList, setSignInerrorList] = useState("");      
 
  const [image, setImage] = useState(null);
   
  useEffect(() => {
    (async () => {
      if (Platform.OS !== "web") {
        const {
          status,
        } = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== "granted") {
          alert("Sorry, we need camera roll permissions to make this work!");
        }
      }
    })();
  }, []);

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      base64: true,
      aspect: [4, 4],
      quality: 1,
    });

    console.log(result);
    console.log("image asset", result);
    if (!result.cancelled) {
      setImage(result.uri);
    }
  };

  const viewSignUp = () => {
    setsignupView(!signupView);
    
  };

  const signUpUser = async (email, name, password, phone, image) => {
    Keyboard.dismiss();
    if (email && name && password && phone && image) {
      const body = {
        TX_USER_NAME: name,
        TX_USER_EMAIL: email,
        TX_USER_PASSWORD: password,
        TX_USER_PHONE: phone,
        TX_USER_PICTURE: image,
      };
      console.log(baseURLAPI);

      axios
        .post(baseURLAPI + "/auth/register", body)
        .then((response) => {
          if (response.data.jwtToken) {
            AsyncStorage.setItem("jwtToken", response.data.jwtToken);
             props.setLogin();
          }
        })
        .catch((error) => {
          console.log(error);
           this.setRegisterErrorList("An error occured");
        });
    } else {
   // this.setRegisterErrorList("Incomplete details");
    }
  };
  const signInUser = async (email, password) => {
    Keyboard.dismiss();
    if (email && password) {
      const body = {
        TX_USER_NAME: email,
        TX_USER_EMAIL: email,
        TX_USER_PASSWORD: password,
      };
      console.log(baseURLAPI);
      axios
        .post(baseURLAPI + "/auth/login", body)
        .then((response) => {
          if (response.data.jwtToken) {
